def calculate_cage_score(cut_down, annoyed, guilty, eye_opener):
    # Convert inputs to boolean (handles string inputs from LLM)
    def to_bool(val):
        if isinstance(val, bool):
            return val
        if isinstance(val, str):
            return val.lower() in ('true', 'yes', '1', 'y')
        return bool(val)

    cut_down = to_bool(cut_down)
    annoyed = to_bool(annoyed)
    guilty = to_bool(guilty)
    eye_opener = to_bool(eye_opener)

    score = sum([cut_down, annoyed, guilty, eye_opener])

    if score == 0:
        interpretation = 'Low likelihood of alcohol use disorder'
        recommendation = 'No immediate concern'
    elif score == 1:
        interpretation = 'Possible alcohol misuse'
        recommendation = 'Brief intervention, monitor'
    elif score >= 2:
        interpretation = 'Clinically significant alcohol problem likely'
        recommendation = 'Further assessment, referral to addiction services'

    return {
        'score': score,
        'interpretation': interpretation,
        'recommendation': recommendation
    }
