def calculate_creatinine_clearance(age, weight_kg, creatinine, sex):
    '''Calculate CrCl using Cockcroft-Gault'''
    # Convert inputs to proper types (handles string inputs from LLM)
    age = int(age) if not isinstance(age, int) else age
    weight_kg = float(weight_kg) if not isinstance(weight_kg, (int, float)) else weight_kg
    creatinine = float(creatinine) if not isinstance(creatinine, (int, float)) else creatinine
    sex = str(sex)

    if sex.lower() in ['male', 'm']:
        crcl = ((140 - age) * weight_kg) / (72 * creatinine)
    elif sex.lower() in ['female', 'f']:
        crcl = ((140 - age) * weight_kg) / (72 * creatinine) * 0.85
    else:
        return None
    return round(crcl, 1)
