def calculate_curb65(confusion, bun, respiratory_rate, systolic_bp, diastolic_bp, age):
    """Calculate CURB-65 score for pneumonia severity"""

    score = 0

    # C: Confusion
    if confusion:
        score += 1

    # U: Urea (BUN > 19 mg/dL)
    if bun > 19:
        score += 1

    # R: Respiratory rate >= 30
    if respiratory_rate >= 30:
        score += 1

    # B: Blood pressure (systolic <90 OR diastolic <=60)
    if systolic_bp < 90 or diastolic_bp <= 60:
        score += 1

    # 65: Age >= 65
    if age >= 65:
        score += 1

    return score
