def calculate_heart_score(history_score, ecg_score, age, risk_factors_count, troponin_level):
    score = 0

    # History (0-2)
    score += min(2, max(0, history_score))

    # ECG (0-2)
    score += min(2, max(0, ecg_score))

    # Age (0-2)
    if age < 45:
        score += 0
    elif age <= 64:
        score += 1
    else:
        score += 2

    # Risk factors (0-2)
    if risk_factors_count == 0:
        score += 0
    elif risk_factors_count <= 2:
        score += 1
    else:
        score += 2

    # Troponin (0-2)
    if troponin_level.lower() == 'normal':
        score += 0
    elif troponin_level.lower() == 'borderline':
        score += 1
    else:
        score += 2

    # Risk categorization
    if score <= 3:
        risk_category = 'Low Risk'
        mace_risk = '0.9-1.7%'
        recommendation = 'Early discharge, outpatient follow-up'
    elif score <= 6:
        risk_category = 'Moderate Risk'
        mace_risk = '12-17%'
        recommendation = 'Observation, further testing'
    else:
        risk_category = 'High Risk'
        mace_risk = '50-65%'
        recommendation = 'Admission, early invasive strategy'

    return {
        'score': score,
        'risk_category': risk_category,
        'mace_risk_percent': mace_risk,
        'recommendation': recommendation
    }
