def calculate_mean_arterial_pressure(systolic_bp, diastolic_bp):
    """Calculate Mean Arterial Pressure from systolic and diastolic BP"""

    if systolic_bp is None or diastolic_bp is None:
        return None

    map_value = diastolic_bp + (systolic_bp - diastolic_bp) / 3
    return round(map_value, 1)
