def calculate_nihss(loc_consciousness, loc_questions, loc_commands, gaze, visual, facial_palsy, motor_arm_left, motor_arm_right, motor_leg_left, motor_leg_right, limb_ataxia, sensory, language, dysarthria, extinction_inattention):
    score = (loc_consciousness + loc_questions + loc_commands + gaze + visual + 
             facial_palsy + motor_arm_left + motor_arm_right + motor_leg_left + 
             motor_leg_right + limb_ataxia + sensory + language + dysarthria + 
             extinction_inattention)

    if score == 0:
        severity = 'No stroke symptoms'
        prognosis = 'Excellent'
    elif score <= 4:
        severity = 'Minor stroke'
        prognosis = 'Good recovery likely'
    elif score <= 15:
        severity = 'Moderate stroke'
        prognosis = 'Variable recovery'
    elif score <= 20:
        severity = 'Moderate to severe stroke'
        prognosis = 'Poor recovery likely'
    else:
        severity = 'Severe stroke'
        prognosis = 'Very poor recovery'

    return {
        'score': score,
        'severity': severity,
        'prognosis': prognosis
    }
