def calculate_creatinine_clearance(age, weight_kg, serum_creatinine, gender):
    """Calculate creatinine clearance using Cockcroft-Gault equation"""

    if age is None or weight_kg is None or serum_creatinine is None or serum_creatinine == 0:
        return None

    # Cockcroft-Gault: CrCl = ((140 - age) × weight) / (72 × Scr) [× 0.85 if female]
    crcl = ((140 - age) * weight_kg) / (72 * serum_creatinine)

    if gender and gender.upper() == 'F':
        crcl *= 0.85

    return round(crcl, 1)
